INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Red Dead Redemption 2', 4.7, 2018, 'Role-playing (RPG), Adventure', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/2.png', 'Um retrato emocionante do fim do Velho Oeste e da luta por redenção.', 'Em um mundo aberto deslumbrante e detalhado, Red Dead Redemption 2 acompanha Arthur Morgan, membro da gangue Van der Linde, enquanto ele enfrenta traições, lealdades e a inevitável chegada da lei e da ordem. Com uma narrativa poderosa, gráficos impressionantes e mecânicas realistas, o jogo oferece uma experiência imersiva e emocionalmente impactante.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('The Witcher 3: Wild Hunt', 4.7, 2014, 'Role-playing (RPG), Adventure', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/3.png', 'A jornada de um caçador de monstros em um mundo sombrio e cheio de perigos.', 'Geralt de Rívia, um bruxo mutante, embarca em uma busca épica para encontrar sua filha adotiva, Ciri, perseguida pela temível Caçada Selvagem. Com uma narrativa profunda, escolhas significativas e um mundo vivo repleto de criaturas místicas e dilemas éticos, The Witcher 3 é amplamente considerado um dos melhores RPGs já feitos.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Sekiro: Shadows Die Twice', 3.8, 2019, 'Role-playing (RPG), Adventure', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/4.png', 'Em um Japão feudal fantástico, você assume o papel de Wolf, um shinobi ressuscitado com o poder de renascer após a morte. Com combates intensos baseados em parry e movimentação precisa, Sekiro exige habilidade, paciência e reflexos afiados. Uma experiência única que redefine os limites dos jogos de ação.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Ghost of Tsushima', 4.6, 2012, 'Role-playing (RPG), Adventure', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/5.png', 'Jin Sakai, um samurai, deve abandonar seu código para se tornar o "Fantasma" e libertar sua ilha da invasão mongol. Com visuais deslumbrantes inspirados na natureza japonesa e um combate estratégico, Ghost of Tsushima oferece uma narrativa emocionante e um mundo aberto rico em cultura e beleza.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Super Mario World', 4.7, 1990, 'Platform', 'Super Ness, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/6.png', 'No icônico jogo do Super Nintendo, Mario e seu amigo Yoshi exploram o Reino dos Cogumelos em uma jornada cheia de segredos, fases criativas e diversão pura. Com controles precisos e design de níveis brilhante, Super Mario World é um marco dos jogos de plataforma.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Hollow Knight', 4.6, 2017, 'Platform', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/7.png', 'Explore um reino subterrâneo esquecido em busca de segredos e desafios.', 'Neste jogo de ação e exploração em 2D, você controla um cavaleiro silencioso em Hallownest, um vasto mundo sombrio cheio de criaturas estranhas, chefes desafiadores e uma atmosfera melancólica. Com arte desenhada à mão e trilha sonora envolvente, Hollow Knight é uma obra-prima do gênero metroidvania.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Ori and the Blind Forest', 4, 2015, 'Platform', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/8.png', 'Uma jornada emocionante de luz, natureza e sacrifício.', 'Ori, uma criatura espiritual, deve restaurar a Floresta Nibel após um desastre natural. Com plataformas precisas, poderes mágicos e uma história tocante, este jogo combina desafio e beleza visual em uma experiência cinematográfica e memorável.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Cuphead', 4.6, 2017, 'Platform', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/9.png', 'Um desafio intenso com arte inspirada nos desenhos animados dos anos 1930.', 'Cuphead e seu irmão Mugman devem enfrentar uma série de chefes coloridos e implacáveis para pagar suas dívidas com o diabo. Com animação feita à mão, trilha sonora jazzística e mecânicas de combate precisas, o jogo é uma homenagem ao estilo clássico e ao espírito dos jogos hardcore.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Sonic CD', 4, 1993, 'Platform', 'Sega CD, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/10.png', 'Corra no tempo com o ouriço mais rápido do mundo.', 'Sonic the Hedgehog viaja no tempo para impedir que o Dr. Robotnik destrua o planeta. Com zonas vibrantes, música eletrizante e a inovadora mecânica de viagem no tempo, Sonic CD é um dos títulos mais queridos da era 16-bit, oferecendo múltiplos finais e fases secretas.');